mapper.select:
select id, name, originalPrice, salePrice, tradeVolume, satisfaction, mealSign, lineNumber, gobackWay, aheadApply, payWay, lineFeature, costInclude, costUnInclude, ContractPay, remark, telephone, lineImage
from line
where name =#{name}

select id, name, originalPrice, salePrice, tradeVolume, satisfaction, mealSign, lineNumber, gobackWay, aheadApply, payWay, lineFeature, costInclude, costUnInclude, ContractPay, remark, telephone, lineImage
from line
where id =#{id}

select id, lineId, day, name, meal, stay, trafic, detail
from line_schedule
where lineId =#{lineId}
